sections.css:
@import "base/body"
@import "base/form"
@import "base/helper_classes"
@import "base/responsive-utilities"
@import "base/blockquote"
@import "base/headings"
@import "base/link"
@import "base/list"
@import "base/label"
@import "base/paragraph"

// @import url(https://fonts.googleapis.com/css?family=Roboto:400,300,700&subset=latin,greek,greek-ext)
// @import url(https://fonts.googleapis.com/css?family=Roboto+Condensed:400,300&subset=latin,greek)
// @import url(https://fonts.googleapis.com/css?family=Roboto+Slab:400,700&subset=latin,greek-ext,greek)
@import url(https://fonts.googleapis.com/css?family=Open+Sans:400,600,800)

@import "bootstrap/alert"
@import "bootstrap/breadcrumb"
@import "bootstrap/buttons"
@import "bootstrap/carousel"
@import "bootstrap/jumbotron"
@import "bootstrap/label"
@import "bootstrap/list-inline"
@import "bootstrap/modal"
@import "bootstrap/nav-pills"
@import "bootstrap/nav-tabs"
@import "bootstrap/navbar"
@import "bootstrap/pagination"
@import "bootstrap/panel"
@import "bootstrap/table"

// Component
// --------------------------------------------------
.label
  padding: 4px 10px !important

  // font-size: $font-size-base;
  font-weight: $font-weight

  display: inline-block


.label-default
  background-color: $azure

@import "layouts/layout"
@import "layouts/layout--1"
@import "layouts/layout--2"
@import "layouts/layout--3"
@import "layouts/layout--4"
@import "layouts/layout--5"
@import "layouts/layout--6"
@import "layouts/layout--7"

// Sections
@import "layouts/sections/section--footer"

@import "components/block"
@import "components/bookmark"
@import "components/cloudlet"
@import "components/counter"
@import "components/ec-header"
@import "components/featured"
@import "components/field"
@import "components/file"
@import "components/icon"
@import "components/infobox"
@import "components/input-inner-addon"
@import "components/listing"
@import "components/live"
@import "components/logo"
@import "components/middle-align"
@import "components/nav-horizontal"
@import "components/nav-sectiontitles"
@import "components/nav-subway"
@import "components/nav-tiles"
@import "components/nav-vertical"
@import "components/news-carousel"
@import "components/notification-counter"
@import "components/ornament"
@import "components/page-title"
@import "components/ribbon"
@import "components/slogan"
@import "components/social-links"
@import "components/switch"
@import "components/timeline"
@import "components/title-more"

// Component
// --------------------------------------------------
.file
  padding-left: 30px

  position: relative

  display: block


// Dependencies
// --------------------------------------------------
.file .icon
  position: absolute

  font-size: 50px

  top: -5px

  left: -15px

// Component
// --------------------------------------------------
.ornament
  &:before
    content: ''

    display: block

    width: 30px

    height: 4px

    margin-bottom: 30px

    background: $ornament-default


// Modifiers
// --------------------------------------------------
.ornament--azure:before
  background-color: $azure


// Dependencies
// --------------------------------------------------
.ornament.page-title
  margin-top: 0

// Component
// --------------------------------------------------
.page-title

// Modifiers
// --------------------------------------------------
.page-title--no-underline
  border-bottom: none

  margin-bottom: 0


.page-title--condensed
  padding-bottom: 5px

// Component
// --------------------------------------------------
.title-more
  width: 100%

  +middle-align


// Elements
// --------------------------------------------------
.title-more__row
  // Row settings inherit from middle-align mixin

.title-more__col
  // Col settings inherit from middle-align mixin
  vertical-align: bottom


// Dependencies
// --------------------------------------------------
.title-more
  h1, h2, h3, h4, h5, h6
    padding-bottom: 0

  .btn
    float: right

